import React, { Component } from "react";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


toast.configure();
class App extends Component {
    constructor(){
      super();
      this.state = {
        id: '',
        first_name: '',
        last_name:'',
        donations: '',
        total: '',
        image: '',
        des: ''
      };
      this.addDonor = this.addDonor.bind(this);
      this.handleChange = this.handleChange.bind(this);
    }

  addDonor(e){
    fetch('http://localhost:5000/api',{
      method: 'POST',
      body: JSON.stringify(this.state),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then( res =>  res.json())
    .then(data => {
      console.log(data);
      if( data === 'Donante creado'){
        toast.success(data);
      }else{
        toast.error(data);
      }

    }) //respuesta
    .catch(err => console.log(err));
    e.preventDefault();
    e.target.reset();

  }
  handleChange(e) {
    const {name, value} = e.target;
    this.setState({
      [name] : value
    });
  }
  componentDidMount(){
    this.fetchDonor();
  }

  fetchDonor(){
    fetch('http://localhost:5000/api/')
    .then( res => res.json())
    .then( data => console.log(data))
    .catch( err => console.log(err, 'linea 56'));
  }
  render() {
    return (
      <div className="container">
        {/* inpunt donante */}
        <div className="border border-success rounded mt-5 p-4">
          <h1 className="text-center text-success">
            <i className="bi bi-person-plus-fill "></i> Ingrese un nuevo donante
          </h1>
          <form onSubmit={this.addDonor}  className="form-row">
            <div className="form-group col-md-1">
              <input
                type="number"
                name="id"
                placeholder="Id"
                className="form-control"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-md-6">
              <input
                type="text"
                name="first_name"
                placeholder="Nombre donante"
                className="form-control"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-md-5">
              <input
                type="text"
                name="last_name"
                placeholder="Apellido del donate"
                className="form-control"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-md-2">
              <input
                type="number"
                name="donations"
                placeholder=" # donate"
                className="form-control"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-md-3">
              <input
                type="number"
                min="0"
                name="total"
                placeholder="valor"
                className="form-control"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-md-7">
              <input
                type="text"
                name="image"
                placeholder="url de la imagen"
                className="form-control"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-md-10">
              <textarea
                className="form-control"
                placeholder="Ingrese una descripcion"
                name="description"
                onChange={this.handleChange}
              ></textarea>
            </div>

            <div className="form-group col-md-2">
              <button type="submit" className="btn btn-outline-success">
                <i className="bi bi-person-check-fill"></i>Agregar un nuevo donante
              </button>
            </div>
          </form>
        </div>


      </div>
    );
  }
}

export default App;
