import React, { Component } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

toast.configure();
class App extends Component {
  constructor() {
    super();
    this.state = {
      id: "",
      first_name: "",
      last_name: "",
      donations: "",
      total: "",
      image: "",
      des: "",
      donors : [],
      pages: 1
    };
    this.addDonor = this.addDonor.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  addDonor(e) {
    fetch("http://localhost:5000/api", {
      method: "POST",
      body: JSON.stringify(this.state),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data === "Donante creado") {
          toast.success(data);
        } else {
          toast.error(data);
        }
        this.fetchDonor();
      }) //respuesta
      .catch((err) => console.log(err));
    e.preventDefault();
    e.target.reset();
  }
  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  }

  componentDidMount() {
    this.countPageDonor();
    this.fetchDonor();
  }
  countPageDonor(){
    fetch(`http://localhost:5000/api/page`)
      .then((res) => res.json())
      .then((data) => {
       // console.log(data);
        this.setState({pages: data})
        // console.log(this.state.pages);
      })
      .catch((err) => console.log(err));
  }
  deleteDonor(id){
    console.log('elimiminando:', id);
  } 


  fetchDonor() {
    fetch(`http://localhost:5000/api/page14`)
      .then((res) => res.json())
      .then((data) => {
       // console.log(data);
        this.setState({donors: data})
      })
      .catch((err) => console.log(err));
  }

  render() {
    return (
      <div className="container">
        {/* inpunt donante */}
        <div className="row">
          <div className="border border-success rounded mt-5 p-4">
            <h1 className="text-center text-success">
              <i className="bi bi-person-plus-fill "></i> Ingrese un nuevo
              donante
            </h1>
            <form onSubmit={this.addDonor} className="form-row">
              <div className="form-group col-md-1">
                <input
                  type="number"
                  name="id"
                  placeholder="Id"
                  className="form-control"
                  onChange={this.handleChange}
                />
              </div>
              <div className="form-group col-md-6">
                <input
                  type="text"
                  name="first_name"
                  placeholder="Nombre donante"
                  className="form-control"
                  onChange={this.handleChange}
                />
              </div>
              <div className="form-group col-md-5">
                <input
                  type="text"
                  name="last_name"
                  placeholder="Apellido del donate"
                  className="form-control"
                  onChange={this.handleChange}
                />
              </div>
              <div className="form-group col-md-2">
                <input
                  type="number"
                  name="donations"
                  placeholder=" # donate"
                  className="form-control"
                  onChange={this.handleChange}
                />
              </div>
              <div className="form-group col-md-3">
                <input
                  type="number"
                  min="0"
                  name="total"
                  placeholder="valor"
                  className="form-control"
                  onChange={this.handleChange}
                />
              </div>
              <div className="form-group col-md-7">
                <input
                  type="text"
                  name="image"
                  placeholder="url de la imagen"
                  className="form-control"
                  onChange={this.handleChange}
                />
              </div>
              <div className="form-group col-md-10">
                <textarea
                  className="form-control"
                  placeholder="Ingrese una descripcion"
                  name="description"
                  onChange={this.handleChange}
                ></textarea>
              </div>

              <div className="form-group col-md-2">
                <button type="submit" className="btn btn-outline-success">
                  <i className="bi bi-person-check-fill"></i>Agregar un nuevo
                  donante
                </button>
              </div>
            </form>
          </div>

          <div className="col-md-7"></div>
          <div className="col-md-5 justify-content-end mt-2 ">    
                      <form  onSubmit={this.addDonor} className="input-group">
                        <div className="input-group-prepend">
                            <div className="input-group-text" id="btnGroupAddon">PÃ¡ginas {this.state.pages}</div>
                        </div>

                        <input type="text" className="form-control" placeholder="ir a la pagina..."  name="spage" aria-label="Input group example" aria-describedby="btnGroupAddon"/>
                        <button type="submit" className="btn btn-secondary ml-1">OK</button>

                      </form>
            </div>   
        </div>


        <div className="row">
                <h4 className="mt-4 text-center text-danger">Lista de Donantes</h4>
            <table className="table table-sm">
                <thead>
                    <tr className="text-danger text-center">
                        <th scope="col"> Id</th>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>donacion</th>
                        <th>total</th>
                        <th>imagen</th>
                        <th>descripcion</th>
                        <th>Editar</th>
                        <th>Eliminar</th>
                    </tr>
                </thead>
                <tbody className="text-center">


                   {
                     this.state.donors.map( donor => {
                        return (
                          <tr>
                            <td>{donor.id}</td>
                            <td>{donor.first_name}</td>
                            <td>{donor.last_name}</td>
                            <td>{donor.donations}</td>
                            <td>{donor.total.$numberDecimal}</td>
                            <td><img src={donor.image}  width="50" height="50"/></td>
                            <td>{donor.description}</td>
                            <td><button className="btn btn-outline-warning"> <i className="bi bi-person-dash-fill"></i> Eliminar</button>  </td>
                            <td><button className="btn btn-outline-danger" onClick={() => this.deleteDonor(donor._id)} > <i className="bi bi-person-dash-fill"></i> Eliminar</button>  </td>
                          </tr>

                        )
                     })
                   }

                </tbody>
            </table>


        </div>
      </div>
    );
  }
}

export default App;
